package org.usfirst.frc2813.Robot2018.commands.solenoid;
import org.usfirst.frc2813.Robot2018.commands.GearheadsInstantCommand;
import org.usfirst.frc2813.logging.LogType;
import org.usfirst.frc2813.logging.Logger;

import edu.wpi.first.wpilibj.Solenoid;
import edu.wpi.first.wpilibj.command.InstantCommand;

/**
 *
 */
public class SimpleSolenoidToggleStateInstant extends GearheadsInstantCommand {
	/**
	 * Switch the position of the intake pistons
	 */
	private final Solenoid solenoid;

    public SimpleSolenoidToggleStateInstant(Solenoid solenoid) {
    	this.solenoid = solenoid;
    	setName(toString());
    }

	//@Override
	protected void initialize() {
		super.initialize();
        boolean old = solenoid.get();
        Logger.printFormat(LogType.INFO,"%s toggling %s from %b to %b",this,solenoid,old,!old);
        solenoid.set(!old);
	}

    public String toString() {
        return getClass().getSimpleName() + "(" + solenoid + ")";
    }
}

